class PostComments{constructor(e){this.postId=e,this.postContainer=$(`#post-${e}`),this.newCommentForm=$(`#post-${e}-comments-form`),this.createComment(e);let t=this;$(".delete-comment",this.postContainer).each(function(){t.deleteComment($(this))})}createComment(e){let t=this;this.newCommentForm.submit(function(n){n.preventDefault();$.ajax({type:"post",url:"/comments/create",data:$(this).serialize(),success:function(n){let o=t.newCommentDom(n.data.comment);$(`#post-comments-${e}`).prepend(o),t.deleteComment($(".delete-comment",o)),new ToggleLike($(" .toggle-like-button",o)),$(".comment-text-area").val(""),new Noty({theme:"semanticui",text:"Comment published! AJAX",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})})}newCommentDom(e){return $(`\n            \n        <li id="comment-${e._id}">\n            <div class="comment-user">\n                <p>\n                    ${e.user.name}\n                    <br>\n                    ${new Date(e.createdAt).toString().substring(4,21)}\n                </p>\n                <div class="comment-actions">\n                        <a  href="/likes/toggle/?id=${e._id}&type=Comment" class="toggle-like-button" data-likes="0">\n                            0 <i class="fa-regular fa-heart"></i>\n                        </a>\n\n                        <a href="comments/delete/${e._id}" class="delete-comment" id="<%= comment._id  %>"><i\n                        class="fa-regular fa-trash-can"></i></a>\n                   \n        </div>\n            </div>\n            <div class="comment-content">\n                <p>\n                ${e.content}\n                </p>\n                \n            </div>\n        </li>\n            \n            `)}deleteComment(e){$(e).click(function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){$(`#comment-${e.data.comment_id}`).remove(),new Noty({theme:"semanticui",text:"Comment Deleted AJAX",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})})}}